import React from 'react';
import { useHistory, useParams } from 'react-router-dom';

import { IonContent, IonPage, IonGrid, IonRow, IonCol, IonHeader, IonToolbar, IonTitle, IonApp } from '@ionic/react';
import NavBar from '../Complement/NavBar';
import Tableaux from '../components/Tableaux';

const Accueil: React.FC = () => {

    const { etat } = useParams();

    const renderContent = () => {
        if (etat === 'general' ) {
          // Affiche cela si la condition est vraie
          return <div>Contenu lorsque la condition est vraie</div>;
        } else if(etat === 'attack' ) {
          // Affiche cela si la condition est fausse
          return <div>Contenu lorsque la condition est fausse</div>;
        }
      };


  <IonApp>
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <NavBar />
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">

        <Tableaux/>
      </IonContent>
    </IonPage>
  </IonApp>
};

export default Accueil;
